ruleNextPage:
@js:
try{
		u = String(java.get('url'))
		p = u.match(/pg=(\d+)&/)[1]
		p = 'pg='+(parseInt(p)+1)+'&'
		url = u.replace(/pg=(\d+)&/,p)
	}
catch(err){url=""}
url

